mapper.select:
select * from user_info where 1 = 1

    and user_id = #{userId}


    and user_id_card = #{userIdCard}

and rec_status = 1

mapper.update:
update user_info

    #{userPassword}
    #{userGender}
    #{userAge}
    #{userTitle}
    #{userCollege}
    #{userProfession}
    #{userClass}
    #{userAddress}
    #{userEmail}
    #{userPhoneNumber}
    #{recStatus}
    #{ext1}
    #{ext2}
    #{ext3}

where 1 = 1

    and user_id = #{userId}


    and user_id_card = #{userIdCard}

and rec_status = 1
